var Preloader;

import {
  modeAssets
} from '../assets';

export default Preloader = class Preloader extends Phaser.State {
  preload() {
    this.showSplashScreen();
    return this.load.pack('modeAssets', null, {modeAssets});
  }

  create() {
    return setTimeout(this.state.start.bind(this.state, 'Mode'), 3000);
  }

  showSplashScreen() {
    var loaderani;
    this.add.image(0, 0, 'splash-screen');
    loaderani = this.add.tileSprite(275, 300, 100, 100, 'progress-bar');
    loaderani.animations.add('spin');
    loaderani.animations.play('spin', 32, true);
    this.font = this.add.retroFont('knight3', 31, 25, Phaser.RetroFont.TEXT_SET6, 10, 1, 1);
    this.font.text = 'TIC TAC TOE';
    this.title = this.add.image(this.world.centerX, 490, this.font);
    this.title.tint = Math.random() * 0xFFFFFF;
    return this.title.anchor.set(0.5, 1);
  }

  update() {
    return this.title.tint = Math.random() * 0xFFFFFF;
  }

};

